angular.module("todoApp",[]).controller("TodoListController",function(){var n=this;n.todoText="";n.todos=[{text:"learn Angular",done:!0},{text:"build an angular app",done:!1}];n.addTodo=function(){n.todos.push({text:n.todoText,done:!1});n.todoText=""};n.remaining=function(){var t=0;return angular.forEach(n.todos,function(n){t+=n.done?0:1}),t};n.archive=function(){var t=n.todos;n.todos=[];angular.forEach(t,function(t){t.done||n.todos.push(t)})}});